<?php

namespace App\Repository;

/**
 * PostulantRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class PostulantRepository extends \Doctrine\ORM\EntityRepository
{



	public function findByDossiertypeactif($typeactif){

		$qb = $this
			->createQueryBuilder('p')
            ->join('p.dossier', 'd')
            ->addSelect('d')
            ->where('d.typeactif = :typeactif')
            ->setParameters(array(':typeactif'=> $typeactif));
		return $qb->getQuery()->getResult();
	}


}
